mapper.update:
UPDATE  tb_board
   SET  board_hit = board_hit +1
 WHERE  board_no = #{board_no}

mapper.select:
SELECT  count(*)
  FROM  tb_board 
 WHERE  board_no = #{board_no}
   		AND board_pw = #{board_pw}

mapper.insert:
INSERT  
  INTO 
		 tb_board
				(  board_no
				 , board_title
				 , board_cont
				 , board_writer
				 , board_date
				 , board_pw)
	   VALUES
				( (SELECT COALESCE(MAX(board_no), 0) + 1 FROM tb_board)
				 , #{board_title}
				 , #{board_cont}
				 , #{board_writer}
				 , CURRENT_DATE
				 , #{board_pw}
				);

mapper.update:
UPDATE
		 tb_board
   SET
		 board_title = #{board_title}
		,board_cont = #{board_cont}
		,board_writer = #{board_writer}
		,board_date = CURRENT_DATE
		,board_pw = #{board_pw}
 WHERE
		 board_no = #{board_no}
