mapper.select:
SELECT  sido_nm, sido_cd
  FROM  tb_sido
 WHERE  sido_cd = #{sido_cd}

SELECT hk.kind_cd ,COUNT(hk.kind_cd) AS kind_tot, hk.kind_nm FROM tb_hospital hp JOIN tb_hoskind hk ON hp.kind_cd = hk.kind_cd
and hp.sido_cd = #{sido_cd}
GROUP BY hk.kind_cd ORDER BY hk.kind_cd

		SELECT  sigungu_cd, sigungu_nm, sido_cd
 		  FROM  tb_sigungu sgg
 		 WHERE  sido_cd = #{sido_cd}

	SELECT  hp.hos_nm
	  FROM  tb_hospital hp



	  			and hp.sigungu_cd = #{sigungu_cd}


	  			and hp.kind_cd = #{kind_cd}


	  			and hp.hos_nm Like CONCAT('%', #{hos_nm}, '%')


	ORDER BY hp.hos_nm asc
     LIMIT   #{pageSize}
    OFFSET   #{pageindex}

SELECT  count(*)
  FROM  tb_hospital hp



  			and hp.sigungu_cd = #{sigungu_cd}


  			and hp.kind_cd = #{kind_cd}


  			and hp.hos_nm Like CONCAT('%', #{hos_nm}, '%')
